package fr.isima.webdev
import fr.isima.webdev.Badge.DefinedBadge
import sun.misc.BASE64Encoder

class User {

	transient springSecurityService
/* Security */
	String username
	String password
	boolean enabled = true
	boolean accountExpired
	boolean accountLocked
	boolean passwordExpired


/* user infos */
	String firstname
	String lastname
	String bio
	String mail
	byte[] photoProfile
	int score = 0





	static hasMany = [votes : Vote,messages : Message,badges : Badge]
	static transients = ['springSecurityService']

	static constraints = {
		username blank: false, unique: true
		password blank: false

		mail blank:false, unique:true,email:true
		bio nullable: true
		firstname nullable: true
		lastname nullable: true
		photoProfile maxSize: 100 * 1024, nullable: true

	}

	static mapping = {
		password column: '`password`'
		bio type: 'text'
		votes cascade: 'all-delete-orphan'
		messages cascade: 'all-delete-orphan'
	}

	Set<Role> getAuthorities() {
		UserRole.findAllByUser(this).collect { it.role }
	}

	def beforeInsert() {
		encodePassword()
	}

	def beforeUpdate() {
		if (isDirty('password')) {
			encodePassword()
		}
	}

	protected void encodePassword() {
		password = springSecurityService?.passwordEncoder ? springSecurityService.encodePassword(password) : password
	}

	def String toString(){
		return username
	}

	def String photoString(){

		String str;
		try {
			str ="data:image/png;base64," + new BASE64Encoder().encode(this.photoProfile)
		}catch (Exception e){
			str = "data:image/png;base64,"
		}
		return str
	}

	def int numberOfQuestions(){
		int compt = 0;
		for(Message mess : this.messages){
			if(mess instanceof Topic){
				compt++;
			}
		}
		return compt;
	}

	def int numberOfResponses(){
		int compt = 0;
		for(Message mess : this.messages){
			if(mess instanceof Response){
				compt++;
			}
		}
		return compt;
	}

	def boolean hasBadge(DefinedBadge inBadge){
		for (Badge badge : this.badges){
			if(badge.badge == inBadge){
				return true;
			}
		}
		return false
	}

	def List<Topic> listTopics(){
		this.messages.grep({it instanceof Topic}).toList()
	}

	def List<Response> listResponses(){
		this.messages.grep({it instanceof Response}).toList()
	}

	def List<Comment> listComments(){
		this.messages.grep({it instanceof Comment}).toList()
	}
}
